CREATE TABLE IF NOT EXISTS `web_breves` (
  `brev_id` int(12) NOT NULL auto_increment,
  `brev_titulo` text collate utf8_unicode_ci NOT NULL,
  `brev_text` text collate utf8_unicode_ci NOT NULL,
  `brev_imagen` tinytext collate utf8_unicode_ci NOT NULL,
  `brev_cat` tinytext collate utf8_unicode_ci NOT NULL,
  `brev_pub` tinytext collate utf8_unicode_ci NOT NULL,
  `brev_dest` tinytext collate utf8_unicode_ci NOT NULL,
  `brev_fecha` varchar(255) collate utf8_unicode_ci NOT NULL default '',
  PRIMARY KEY  (`brev_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=27 ;

