CREATE TABLE IF NOT EXISTS `web_testimonios` (
  `test_id` int(12) NOT NULL auto_increment,
  `test_nombre` text collate utf8_unicode_ci NOT NULL,
  `test_text` text collate utf8_unicode_ci NOT NULL,
  `test_pub` tinytext collate utf8_unicode_ci NOT NULL,
  PRIMARY KEY  (`test_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=15 ;

--
-- Volcar la base de datos para la tabla `web_testimonios`
--

INSERT INTO `web_testimonios` (`test_id`, `test_nombre`, `test_text`, `test_pub`) VALUES
(1, 'Mariana, t&eacute;cnico en turismo, 38 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt;&amp;quot;Enfrento con m&amp;aacute;s calma las situaciones dif&amp;iacute;ciles; lo que m&amp;aacute;s me afectaba era la depresi&amp;oacute;n, aunque sigo con remedios, siento que esta t&amp;eacute;cnica me ayuda mucho a superarla. He disminuido la dosis de remedios y el psic&amp;oacute;logo me encuentra mejor y me recomienda que siga practicando relaxacci&amp;oacute;n. La gente me encuentra mejor aspecto; tanto hombres como mujeres me han dicho que estoy m&amp;aacute;s linda&amp;quot;.&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(2, 'Teresa, periodista, 35 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt;&amp;quot;Me he sentido con m&amp;aacute;s &amp;aacute;nimo; mi salud ha mejorado, estaba con un problema de tiroides y no me ha molestado; me siento m&amp;aacute;s eficiente, l&amp;uacute;cida y tranquila; menos aprehensi&amp;oacute;n&amp;quot;.&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(3, 'Macarena, publicista, 23 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt;&amp;quot;Fant&amp;aacute;stico, desde que empec&amp;eacute; no he tenido discusiones violentas; la actividad laboral no se me hace tan pesada; acepto m&amp;aacute;s las diferencias de opini&amp;oacute;n, tengo mejor trato con clientes y familia.&amp;quot;&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(4, 'Mar&iacute;&shy;a, periodista, 44 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt; &amp;quot;Me siento m&amp;aacute;s realizada, m&amp;aacute;s energ&amp;iacute;a, logro relacionarme mejor.&amp;quot;&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(5, 'Soledad, abogado, 33 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt; &amp;quot;Mayor eficiencia y tranquilidad para abordar conflictos, lo que es muy importante para mi profesi&amp;oacute;n.&amp;quot;&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(6, 'Luc&iacute;a, empleada dom&eacute;stica, 44 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt; &amp;quot;Mayor disposici&amp;oacute;n para trabajar; he dejado de depender de varios medicamentos (somn&amp;iacute;feros y para la presi&amp;oacute;n); puedo hablar con m&amp;aacute;s tranquilidad, sin alterarme con la otra persona.&amp;quot;&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(7, 'Paz, ingeniero, 33 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt; &amp;quot;Estoy m&amp;aacute;s relajada y compenetrada en mis propias motivaciones; inter&amp;eacute;s por la vida, alegr&amp;iacute;a; ha mejorado mi salud, no he tenido irritaciones cut&amp;aacute;neas ni propensi&amp;oacute;n al catarro, menos s&amp;iacute;ntomas al&amp;eacute;rgicos en general; m&amp;aacute;s lucidez, desarrollo de la creatividad y mayor capacidad de trabajo.&amp;quot;&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(8, 'Bernardo, ejecutivo de una planta industrial, 44 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt; &amp;quot;Noto que los dolores de cabeza (de la hipertensi&amp;oacute;n) han disminuido en intensidad y frecuencia; estoy menos irritable y menos acelerado.&amp;quot;&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(9, 'Mario, ingeniero civil, 41 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt; &amp;quot;Estoy con m&amp;aacute;s &amp;aacute;nimo para enfrentar situaciones nuevas, m&amp;aacute;s optimismo, m&amp;aacute;s creatividad; tolero m&amp;aacute;s los errores de los dem&amp;aacute;s, no me irritan.&amp;quot;&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(10, 'Carmen, agente de viajes, 32 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt; &amp;quot;Siento m&amp;aacute;s &amp;aacute;nimo y energ&amp;iacute;a para enfrentar los problemas, no me complico con peque&amp;ntilde;eces; en 3 semanas he disminuido naturalmente el consumo de cigarrillos (de 20 a 4 &amp;oacute; 5 al d&amp;iacute;a)&amp;quot;.&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(11, 'Patricia, estudiante universitaria, 21 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt; &amp;quot;Siento mucho m&amp;aacute;s lucidez, sobre todo en la Universidad; han mejorado mis relaciones personales, mi irritabilidad ha ido desapareciendo notablemente; mi capacidad de memorizar se ha superado.&amp;quot;&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(12, 'Isabel, M&eacute;dico, 41 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt; &amp;quot;Estoy mejor, m&amp;aacute;s alegre, asertiva, entusiasta y con m&amp;aacute;s energ&amp;iacute;a; m&amp;aacute;s eficiencia, lucidez y tranquilidad; m&amp;aacute;s disfrute al realizar mis actividades. En el hospital las auxiliares tienden a colaborar m&amp;aacute;s espont&amp;aacute;neamente conmigo&amp;quot;.&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(13, 'Mar&iacute;&shy;a S., secretaria, 26 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt; &amp;quot;Desde que empec&amp;eacute; me he ido sintiendo cada vez mejor. He podido terminar exitosamente mis estudios de ingl&amp;eacute;s y he encontrado la energ&amp;iacute;a para preparar mi ingreso a la universidad a estudiar psicolog&amp;iacute;a&amp;quot;.&lt;/p&gt;&lt;/blockquote&gt;', 'si'),
(14, 'Manuel, Abogado, 62 a&ntilde;os', '&lt;blockquote&gt;&lt;p&gt; &amp;quot;Estoy mejor, no s&amp;oacute;lo porque as&amp;iacute; lo siento, sino porque terceros me lo han indicado; mi salud ha mejorado, me siento m&amp;aacute;s liviano, m&amp;aacute;s claro. He notado que puedo expresar mis opiniones m&amp;aacute;s libremente y no quedarme con esa sensaci&amp;oacute;n de no haber dicho todo lo que necesitaba decir. Todo esto en un tono tranquilo, pero eficaz&amp;quot;.&lt;/p&gt;&lt;/blockquote&gt;', 'si');

